package bittorrent.client.tcpMessage;

import static org.junit.jupiter.api.Assertions.*;

import java.io.ByteArrayInputStream;
import java.io.DataInputStream;
import java.io.IOException;

import org.junit.jupiter.api.Test;

import bittorrent.client.Utils;

public class BittorrentMessageTest {
    // Test values
    public static final byte[] validBitfieldMessage  = Utils.hexStringToByteArray("00000003050000");
    public static final byte[] validInterstedMessage = Utils.hexStringToByteArray("0000000102");
    public static final byte[] validUnchokeMessage = Utils.hexStringToByteArray("0000000101");
    public static final byte[] validRequestMessage = Utils.hexStringToByteArray("0000000d06000000060000000000004000");
    public static final byte[] validPieceMessage = Utils.hexStringToByteArray("");
    public static final byte[] validHaveMessage = Utils.hexStringToByteArray("000000050400000006");
    public static final byte[] validNotInterstedMessage = Utils.hexStringToByteArray("0000000103");
    public static final byte[] validKeepAliveMessage = Utils.hexStringToByteArray("00000000");
    public static final byte[] validChokeMessage = Utils.hexStringToByteArray("0000000100");
    public static final byte[] validPortMessage = Utils.hexStringToByteArray("00000003091a20"); // port 6688
    public static final byte[] validCancelMessage = Utils.hexStringToByteArray("0000000d08000000060000000000004000");

    // Test methods
    @Test
    public void testBitfield() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validBitfieldMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(Bitfield.class,message.getClass());
            assertEquals(3,message.messageLength);
            assertEquals(5,message.messageType);
            Bitfield bitfieldMessage = (Bitfield)message;
            assertArrayEquals(Utils.hexStringToByteArray("0000"),bitfieldMessage.bitfield);
            System.out.println(bitfieldMessage);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testInterested() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validInterstedMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(Interested.class,message.getClass());
            assertEquals(1,message.messageLength);
            assertEquals(2,message.messageType);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testUnchoke() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validUnchokeMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(Unchocke.class,message.getClass());
            assertEquals(1,message.messageLength);
            assertEquals(1,message.messageType);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testRequest() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validRequestMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(Request.class,message.getClass());
            assertEquals(13,message.messageLength);
            assertEquals(6,message.messageType);
            // test index, begin, length
            Request requestMessage = (Request)message;
            assertEquals(6,requestMessage.pieceIndex);
            assertEquals(0,requestMessage.pieceBeginOffset);
            assertEquals(16384, requestMessage.pieceLength);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testPiece() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validPieceMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(Piece.class,message.getClass());
            assertEquals(16393,message.messageLength);
            assertEquals(7,message.messageType);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testHave() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validHaveMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(Have.class,message.getClass());
            assertEquals(5,message.messageLength);
            assertEquals(4,message.messageType);
            Have messageHave = (Have)message;
            assertEquals(6,messageHave.pieceIndex);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testNotInterested() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validNotInterstedMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(NotInterested.class,message.getClass());
            assertEquals(1,message.messageLength);
            assertEquals(3,message.messageType);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testKeepAlive() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validKeepAliveMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(KeepAlive.class,message.getClass());
            assertEquals(0,message.messageLength);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testChoke() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validChokeMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(Choke.class,message.getClass());
            assertEquals(1,message.messageLength);
            assertEquals(0,message.messageType);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testPort() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validPortMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(Port.class,message.getClass());
            assertEquals(3,message.messageLength);
            assertEquals(9,message.messageType);
            Port messagePort = (Port)message;
            assertEquals(6688, messagePort.listenPort);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Test
    public void testCancelMessage() {
        try {
            DataInputStream bitfieldInputStream = new DataInputStream(new ByteArrayInputStream(validCancelMessage));
            BittorrentMessage message = new BittorrentMessage(bitfieldInputStream).identify();
            assertEquals(Cancel.class,message.getClass());
            assertEquals(13,message.messageLength);
            assertEquals(8,message.messageType);
            Cancel messageCancel = (Cancel)message;
            assertEquals(6,messageCancel.pieceIndex);
            assertEquals(0,messageCancel.pieceBeginOffset);
            assertEquals(16384,messageCancel.pieceLength);
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }
}
